CREATE TABLE `ezyservices`.`booking_status` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `booking_id` BIGINT NOT NULL , `status` ENUM('PENDING','AWAITING','ACCEPTED','REJECTED','CANCELLED','COMPLETED') NOT NULL , `remarks` TEXT NOT NULL , `status_by` BIGINT NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB; 
