CREATE TABLE `ezyservices`.`payment` ( `id` BIGINT NOT NULL AUTO_INCREMENT , `booking_id` BIGINT NOT NULL , `payment_id` VARCHAR(120) NOT NULL , `order_id` VARCHAR(120) NOT NULL , `amount` INT NOT NULL , `email` VARCHAR(120) NOT NULL , `phone` INT(10) NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `update_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`)) ENGINE = InnoDB; 

ALTER TABLE `payment` ADD FOREIGN KEY (`booking_id`) REFERENCES `booking`(`id`) ON DELETE RESTRICT ON UPDATE RESTRICT; 