CREATE TABLE `ezyservices`.`service` ( `id` BIGINT(20) NOT NULL AUTO_INCREMENT , `name` VARCHAR(120) NOT NULL , `is_active` TINYINT NOT NULL DEFAULT '1' , `slot` INT NOT NULL DEFAULT '1' , `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` DATETIME on update CURRENT_TIMESTAMP NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB; 

ALTER TABLE `service` CHANGE `updated_at` `updated_at` DATETIME on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP; 